import { React, useEffect, useState} from 'react'
import { gapi } from 'gapi-script';
import GoogleLogin from 'react-google-login';
import TodoItem from './Input/TodoItem';
import Card from './UI/Card';


const clientId = '1018055346634-69m89sl2kgajlg79u5k7rd5q4v810vf0.apps.googleusercontent.com'

function Google() {
    const [ profile, setProfile ] = useState(false);

    useEffect(() => {
        const initClient = () => {
              gapi.client.init({
              clientId: clientId,
              scope: ''
            });
         };
         gapi.load('client:auth2', initClient);
     });

    const onSuccess = (res) => {
      console.log('success:', res);
      setProfile(true);
    };

  const onFailure = (error) => {
    console.error(error);
  }

  return (
    <Card>

      {profile && <TodoItem></TodoItem>}

      {!profile && 

        <div>
          <h1> Welcome To Our App </h1>
          <p> Please Use Your Google Account to Login</p>
        </div>
      } 

      {!profile &&
        <GoogleLogin
          clientId= '1018055346634-69m89sl2kgajlg79u5k7rd5q4v810vf0.apps.googleusercontent.com'
          buttonText="Login with Google"
          onSuccess={onSuccess}
          onFailure={onFailure}
          cookiePolicy={'single_host_origin'}
          isSignedIn={true}
        />
      }

      
    </Card>
  )

  
}

export default Google;
