import React from "react"
import Button from "../UI/Button"
import classes from './TodoItemShow.module.css'

const TodoItemShow = props => {


    const deleteHandler = () => {
        alert('You completed your Task')
        props.onDelete(props.id)
        console.log(props.id)
        
    }

    return (
        <li className={classes['item-show']}>
            {props.children}
            <div className='button'><Button onClick={deleteHandler}>Clear</Button></div>
           
        </li>
    )
}

export default TodoItemShow;
